import React, {Component} from 'react';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom';
import Grid from '@material-ui/core/Grid';

class Pagination extends Component{
    render(){
        const backColorDefault = "#2a3547";
        const textCOlor = "#dadfe8";
        
        const selectedId = window.location.pathname.substr(6);

        const changeColor1 = selectedId === "classrooms";
        const changeColor2 = selectedId === "classes";
        const changeColor3 = selectedId === "teachers";
        const changeColor4 = selectedId === "courses";
        const changeColor5 = selectedId === "table";

        console.log(selectedId);
        return(
            <div>
                <Grid container direction="row" justify="center" alignItems="center" item sm={6} >
                    <ButtonGroup color="primary" aria-label="outlined primary button group">
                        <Button style={{backgroundColor: changeColor1 === true ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                            1- Add Class Rooms
                        </Button>
                        <Button style={{backgroundColor: changeColor2 === true ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                            2- Add Classes
                        </Button>
                        <Button style={{backgroundColor: changeColor3 === true ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                            3- Add Teachers
                        </Button>
                        <Button style={{backgroundColor: changeColor4 === true ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                            4- Add Courses
                        </Button>
                        <Button style={{backgroundColor: changeColor5 === true ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                            5- Time Table
                        </Button>

                        {/* <Button onClick={()=> filterOrder('delivered')} variant={delivered} >Delivered</Button> */}
                    </ButtonGroup>
                </Grid>
            </div>
        );
    }
}

export default Pagination;
