import React, { Component } from 'react';
import {withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import SendIcon from '@material-ui/icons/Send';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

const CssTextField = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'green',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: 'green',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'red',
      },
      '&:hover fieldset': {
        borderColor: 'yellow',
      },
      '&.Mui-focused fieldset': {
        borderColor: 'green',
      },
    },
  },
})(TextField);


class Teachers extends Component {

    state = {
        rooms: [{name: ""}],
        width: 0, height: 0,
        marginLeftTextField: 0 
    }

    handleChange = (e, i) => {
       
    }

    addMoreField = () => {

    }

    handleSubmit = () => {
       
    }

    handleRemove = (i) => {
        
    }



        
    updateDimensions = (i) => {
        this.setState({ width: window.innerWidth, height: window.innerHeight });
        if(i === 1){
            if(window.innerWidth <= 700) 
                this.setState({marginLeftTextField: 20})
        }
    };
    
    componentDidMount() {

        window.addEventListener('resize', () => this.updateDimensions(1));
        if(window.innerWidth <= 700) 
                this.setState({marginLeftTextField: 20})
        // console.log(this.state.width)
    }
    componentWillUnmount() {
        window.removeEventListener('resize', () => this.updateDimensions(0));
        if(window.innerWidth <= 700) 
                this.setState({marginLeftTextField: 20})
        // console.log(this.state.width)
    }
    

    render(){
        const {rooms} = this.state;

        return (
            <form noValidate>
                <Grid container row alignItems="center" item xs={12} style={{marginTop: 60}}>
                    {
                        rooms.map((room, i) => {
                            return (
                                <Grid container key = {i} direction="column" justify="center" alignItems="center" item xs={9} style={{marginTop: 20, marginLeft: this.state.marginLeftTextField * -2}}>  
                                    <Grid container direction="row" justify="flex-end" alignItems="center" spacing={4} item sm={12} style={{marginTop: 10}}>                                    
                                        <Button
                                            variant="contained"
                                            color="secondary"
                                            onClick = {() => this.handleRemove(i)}
                                            style={{marginRight: 20}}
                                        >
                                            X
                                        </Button>
                                        <CssTextField
                                            label = {`Teacher-${i+1} Name`}
                                            variant = "outlined"
                                            id = "custom-css-outlined-input"
                                            value = {room.name}
                                            onChange = {(e) => this.handleChange(e, i)}
                                            size="small"
                                            style={{marginRight: 20}}
                                        />
                                    <p style={{marginRight: 20}} >Monday</p>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                        <Select
                                            style={{marginRight: 20}}
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={1}
                                            // onChange={handleChange}
                                            >
                                            <MenuItem value={0}>0</MenuItem>
                                            <MenuItem value={1}>1</MenuItem>
                                        </Select>
                                    </Grid>
                                </Grid>
                            );
                        })
                    }
                         <Grid container
                            direction="row"
                            justify="flex-start"
                            alignItems="flex-start" 
                            item sm={3}
                            style={{marginTop: 20, marginLeft: this.state.marginLeftTextField * 2}}
                        >
                        <Button onClick={this.addMoreField} variant="contained" color="primary">
                            Add more
                        </Button>
                    </Grid>
                </Grid>
                <Grid container
                    direction="row"
                    justify="center"
                    alignItems="center" 
                    item xs={12}
                    style={{ marginTop: 40}}
                >
                    <Button
                        variant="contained"
                        style={{backgroundColor: '#90ee90'}}
                        endIcon={<SendIcon>send</SendIcon>}
                        onClick = {this.handleSubmit}
                    >
                        Submit and Next
                    </Button>
                </Grid>
            </form>
        );
    }
}

export default Teachers;
