import React, {Component} from 'react';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom';
import Grid from '@material-ui/core/Grid';

class Pagination extends Component{

    state = {
        smallScreen: false
    }

    setMarginLeft = () => {
        if(window.innerWidth <= 700){
            this.setState({smallScreen: true})
        }else{
            this.setState({smallScreen: false})
        }
    }

    updateDimensions = (i) => {
        this.setState({ width: window.innerWidth, height: window.innerHeight });
        if(i === 1){
            this.setMarginLeft();
        }
    };
    
    componentDidMount() {

        window.addEventListener('resize', () => this.updateDimensions(1));
        this.setMarginLeft();
        
    }
    componentWillUnmount() {
        window.removeEventListener('resize', () => this.updateDimensions(0));
        this.setMarginLeft();
        // console.log(this.state.width)
    }

    render(){
        const {smallScreen} = this.state;
        
        const backColorDefault = "#2a3547";
        const textCOlor = "#dadfe8";
        
        const selectedId = window.location.pathname.substr(6);

        const changeColor1 = selectedId === "classrooms";
        const changeColor2 = selectedId === "classes";
        const changeColor3 = selectedId === "teachers";
        const changeColor4 = selectedId === "courses";
        const changeColor5 = selectedId === "table";

        console.log(selectedId);
        return(
            <div>
                <form noValidate>
                    <Grid container style={{marginTop: 150}} direction="row" justify="center" alignItems="center" item sm={12} >
                        <ButtonGroup color="primary" aria-label="outlined primary button group">
                            <Button style={{backgroundColor: changeColor1 ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                                <p style={{fontSize: 12}}>1- Add Class Rooms</p>
                            </Button>
                            <Button style={{backgroundColor: changeColor2 ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                                <p style={{fontSize: 12}}>2- Add Classes</p>
                            </Button>
                            <Button style={{backgroundColor: changeColor3 ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                                <p style={{fontSize: 12}}>3- Add Teachers</p>
                            </Button>
                            <Button style={{backgroundColor: changeColor4 ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                                <p style={{fontSize: 12}}>4- Add Courses</p>
                            </Button>
                            <Button style={{backgroundColor: changeColor5 ? "#0f9ac4" : backColorDefault, color: textCOlor}} >
                                <p style={{fontSize: 12}}>5- Time Table</p>
                            </Button>

                            {/* <Button onClick={()=> filterOrder('delivered')} variant={delivered} >Delivered</Button> */}
                        </ButtonGroup>
                    </Grid>
                </form>
            </div>
        );
    }
}

export default Pagination;
