import React, { Component } from 'react';
import {withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import SendIcon from '@material-ui/icons/Send';


const CssTextField = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'green',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: 'green',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'red',
      },
      '&:hover fieldset': {
        borderColor: 'yellow',
      },
      '&.Mui-focused fieldset': {
        borderColor: 'green',
      },
    },
  },
})(TextField);


class ClassRooms extends Component {

    state = {
        rooms: [{name: ""}]
    }

    handleChange = (e, i) => {
        const rooms = [...this.state.rooms];
        rooms[i].name = e.target.value;
        this.setState({rooms})
    }

    addMoreField = () => {
        const rooms = [...this.state.rooms, {name: ""}]
        this.setState({rooms})
    }

    handleSubmit = () => {
        const roomsArray = [];
        const rooms = [...this.state.rooms];
        rooms.map(room => {
            let roomArray = [room.name, [
                ["Monday", 0, 0, 0, 0, 0, 0, 0, 0],
                ["Tuesday", 0, 0, 0, 0, 0, 0, 0, 0],
                ["Wednesday", 0, 0, 0, 0, 0, 0, 0, 0],
                ["Thursday", 0, 0, 0, 0, 0, 0, 0, 0],
                ["Friday", 0, 0, 0, 0, 0, 0, 0, 0],
            ]];
            roomsArray.push(roomArray);
        })
        this.props.onClassRooms(roomsArray);
        console.log("getWindowDimensions(): ", window.innerWidth)
    }

    render(){
        const {rooms} = this.state;

        return (
            <form noValidate>
                <Grid container style={{marginTop: 60}}>
                    {
                        rooms.map((room, i) => {
                            return (
                                <Grid container key = {i} direction="column" justify="center" alignItems="center" item xs={8} >  
                                    <Grid only="lg" container direction="row" justify="center" alignItems="center" item xs={1} >                                  
                                        <Button
                                            variant="contained"
                                            color="secondary"
                                            onClick = {this.handleRemove}
                                        >
                                            X
                                        </Button>
                                    </Grid>
                                    <Grid container direction="row" justify="flex-end" alignItems="center" item xs={5} style={{marginTop: -40}}>                                    
                                        <CssTextField
                                            label = "Room Name"
                                            variant = "outlined"
                                            id = "custom-css-outlined-input"
                                            value = {rooms.name}
                                            onChange = {(e) => this.handleChange(e, i)}
                                            size="small"
                                        />
                                    </Grid>
                                </Grid>
                            );
                        })
                    }
                         <Grid container
                            direction="row"
                            justify="flex-start"
                            alignItems="flex-start" 
                            item xs={4}
                        >
                        <Button onClick={this.addMoreField} variant="contained" color="primary">
                            Add more
                        </Button>
                    </Grid>
                </Grid>
                <Grid container
                    direction="row"
                    justify="center"
                    alignItems="center" 
                    item xs={12}
                    style={{ marginTop: 40}}
                >
                    <Button
                        variant="contained"
                        color="secondary"
                        endIcon={<SendIcon>send</SendIcon>}
                        onClick = {this.handleSubmit}
                    >
                        Submit and Next
                    </Button>
                </Grid>
            </form>
        );
    }
}

export default ClassRooms;
