    //     if(currentURL != oldURL){
    //         const pathNew = window.location.pathname.substr(6);
    //         if(pathNew === "classrooms"){
    //             ProgressValue = 0;
    //         }else if(pathNew === "classes"){
    //             ProgressValue = 25;
    //         }else if(pathNew === "teachers"){
    //             ProgressValue = 50;
    //         }else if(pathNew === "courses"){
    //             ProgressValue = 75
    //         }else if(pathNew === "table"){
    //             ProgressValue = 100
    //         }

    //         oldURL = currentURL;
    //     }

    //     oldURL = window.location.href;
    //     setInterval(function() {
    //         checkURLchange(window.location.href);
    //     }, 1000);

    // }

    // checkURLchange();
    console.log("ononclassRooms: ", ononclassRooms)

  return (
    <div className={classes.root}>
      <FacebookCircularProgress />
      <br />
      <BorderLinearProgress variant="determinate" value={ProgressValue} />
    </div>
  );
}
